    function getSignature() {
        $.get("/wxRequest/getSignature", {"url": location.href.split('#').toString()}).done(function (data) {
            // 注意这里的url，一定要这样写，也就是动态获取，不然也不会成功的。
            console.log(data);
            if (data.retCode===0) {
                var wx_info = data.data;
                if (wx_info.signature != null) {
                    // 配置
                    wx.config({
                        debug: true,   // 测试阶段，可以写为true，主要是为了测试是否配置成功
                        appId: wx_info.appId,
                        timestamp: wx_info.timestamp,
                        nonceStr: wx_info.nonceStr,
                        signature: wx_info.signature,
                        jsApiList: ['checkJsApi', 'updateAppMessageShareData', 'updateTimelineShareData', 'onMenuShareTimeline', 'onMenuShareAppMessage', 'onMenuShareQQ', 'onMenuShareQZone']
                    });

                    var title = "百卓优采云进销存";
                    var desc = "百卓优采云进销存";
                    // 分享的图片，最好是正方形，不是也没关系，但是一定是http模式，即绝对路径，而不是服务器路劲
                    var imgUrl = "https://erp.abiz.com/images/server/common/lajifenlei.png";
                    // 这里的地址可以写死，也可以动态获取，但是一定不能带有微信分享后的参数，不然分享也是失败的
                    var link = "";

                    // 分享给朋友、QQ、微博
                    var shareData = {
                        "imgUrl": imgUrl,
                        "title": title,
                        "desc": desc,
                        'link': link
                    };
                    // 分享到朋友圈
                    var shareToTimeline = {
                        "imgUrl": imgUrl,
                        "title": title,
                        'link': link,
                        "desc": desc
                    };
                    wx.ready(function() {
                        wx.onMenuShareTimeline(shareToTimeline);
                        wx.onMenuShareAppMessage(shareData);
                        wx.onMenuShareQQ(shareData);
                        wx.onMenuShareQZone(shareData);
                        wx.updateAppMessageShareData(shareData);
                        wx.updateTimelineShareData(shareData);

                        wx.error(function(res) {
                            alert(res.errMsg);
                        });
                    });

                    wx.checkJsApi({
                        jsApiList: ['updateAppMessageShareData', 'updateTimelineShareData', 'onMenuShareTimeline', 'onMenuShareAppMessage', 'onMenuShareQQ', 'onMenuShareQZone'], // 需要检测的JS接口列表，所有JS接口列表见附录2,
                        success: function(res) {
                            // 以键值对的形式返回，可用的api值true，不可用为false
                            // 如：{"checkResult":{"chooseImage":true},"errMsg":"checkJsApi:ok"}
                            alert(JSON.stringify(res.checkResult));
                        }
                    });

                }
            }
        }).fail(function (msg) {
            console.log("error:" + msg);
        });
    }


    $(".js-search").bind("input propertychange",function(event){
         let keyArray = fuzzyQuery(searchArr, $(this).val());
         if(keyArray.length>0 && $(this).val()!=''){
             let liHtml = '';
             keyArray.forEach((item)=>{
                 liHtml = liHtml + '<li data-name="'+item+'">'+item+'<li>'
             });
             $('.js-suggest-ul').html(liHtml);
             $('.js-suggest-ul').show();
         }else{
             $('.js-suggest-ul').hide();
         }

    });

    $(document).on('click','.js-suggest-ul li',function () {
       let name = $(this).data('name');
       $('.js-search').val(name);
        $('.js-suggest-ul').hide();
    });

    $(".js-search-item").click(function(){
        $('.js-search').val($(this).text());
        changeType($(this).text());
    });

    $(".js-ga-query").click(function () {
        changeType($(".js-search").val());
    });

    function changeType(name) {
        $('.js-module').css("visibility","visible");
        var type = ljhsJson[name];
        if(!name || !type){
            $('.js-letter-name').hide();
            $('.js-letter-type').hide();
            $('.js-belong').hide();
            $('.js-error').show();
            $('.js-error').text('内容未找到，请尝试更换或缩短关键词！');
        } else {
            $('.js-letter-name').show();
            $('.js-letter-type').show();
            $('.js-belong').show();
            $('.js-error').hide();
            $('.js-letter-name').text(name);
            $('.js-letter-type').text(type);
        }
    }

    function fuzzyQuery(list, keyWord) {
        var arr = [];
        for (var i = 0; i < list.length; i++) {
            if (list[i].indexOf(keyWord) >= 0) {
                arr.push(list[i]);
            }
        }
        return arr;
    }

